INSERT INTO `articulo` (`fecha_publicacion`, `id`, `usuario`, `contenido`, `descripcion`, `titulo`) VALUES ('2023-12-22', NULL, '35', '<div class="entry-content" itemprop="text" style="box-sizing: inherit; counter-reset: footnotes 0; margin-top: 2em; color: rgb(32, 33, 34); font-family: &quot;Nunito Sans&quot;, sans-serif; font-size: 17px; text-align: start; height: auto !important;"><p class="has-background" style="box-sizing: inherit; margin-bottom: 1.5em; padding: 1.25em 2.375em; border: 0px; background-color: rgb(232, 245, 233);">La sintaxis de&nbsp;<mark class="has-inline-color" style="box-sizing: inherit; font-family: Consolas, Menlo, &quot;courier new&quot;, monospace; font-size: 15px; color: rgb(165, 13, 13);">CREATE DATABASE</mark>&nbsp;es igual<span style="box-sizing: inherit; font-weight: 700;">&nbsp;</span>en los distintos sistemas de bases de datos como SQL Server, MySQL, PostgreSQL o MariaDB.</p><hr class="wp-block-separator has-text-color has-base-color has-alpha-channel-opacity has-base-background-color has-background" style="box-sizing: inherit; margin-top: 40px; margin-bottom: 40px; border-top: 2px solid; border-right-width: initial; border-right-style: none; border-bottom-width: initial; border-bottom-style: none; border-left-width: initial; border-left-style: none; background-color: var(--base); height: 1px;"><h2 class="wp-block-heading" style="box-sizing: inherit; margin-top: 20px; margin-bottom: 30px; border: 0px; font-family: inherit; font-size: 35px; font-style: inherit; line-height: 1.2em;"><span id="create-database-en-sql" style="box-sizing: inherit;">CREATE DATABASE en SQL</span></h2><p style="box-sizing: inherit; margin-bottom: 1.5em; border: 0px;">La sintaxis básica para&nbsp;<span style="box-sizing: inherit; font-weight: 700;">crear una base de datos en SQL</span>&nbsp;con la sentencia&nbsp;<mark class="has-inline-color" style="box-sizing: inherit; font-family: Consolas, Menlo, &quot;courier new&quot;, monospace; font-size: 15px; color: rgb(165, 13, 13);">CREATE DATABASE</mark>&nbsp;es la siguiente:</p><pre class="wp-block" style="box-sizing: inherit; margin: 24px -20px; padding: 20px; border: 0px; background: rgb(244, 244, 244); font-family: inherit; font-size: inherit; line-height: normal; max-width: 100%; border-radius: 10px;"><pre class="wp-block-code" style="margin-top: 10px; margin-bottom: 10px; padding: 20px; border-width: 0px 0px 0px 4px; border-style: solid; border-color: rgb(255, 255, 255) rgb(255, 255, 255) rgb(255, 255, 255) rgb(4, 170, 109); border-image: initial; background: rgb(255, 255, 255); font-family: Consolas, Menlo, &quot;courier new&quot;, monospace; font-size: inherit; line-height: normal; max-width: 100%; color: rgb(0, 0, 0);"><code style="box-sizing: inherit; font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-variant-position: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-stretch: normal; font-size: 15px; line-height: normal; font-family: inherit; display: block; text-wrap: wrap;"><span class="sqlkeyword" style="box-sizing: inherit; color: rgb(59, 120, 231); line-height: 1.5;">CREATE DATABASE</span> nombre_database;</code></pre></pre><hr class="wp-block-separator has-text-color has-base-color has-alpha-channel-opacity has-base-background-color has-background" style="box-sizing: inherit; margin-top: 40px; margin-bottom: 40px; border-top: 2px solid; border-right-width: initial; border-right-style: none; border-bottom-width: initial; border-bottom-style: none; border-left-width: initial; border-left-style: none; background-color: var(--base); height: 1px;"><h2 class="wp-block-heading" style="box-sizing: inherit; margin-top: 20px; margin-bottom: 30px; border: 0px; font-family: inherit; font-size: 35px; font-style: inherit; line-height: 1.2em;"><span id="ejemplo-de-create-database" style="box-sizing: inherit;">Ejemplo de CREATE DATABASE</span></h2><p style="box-sizing: inherit; margin-bottom: 1.5em; border: 0px;">En el siguiente ejemplo se utiliza&nbsp;<mark class="has-inline-color" style="box-sizing: inherit; font-family: Consolas, Menlo, &quot;courier new&quot;, monospace; font-size: 15px; color: rgb(165, 13, 13);">CREATE DATABASE</mark>&nbsp;para crear la base de datos&nbsp;<mark class="has-inline-color" style="box-sizing: inherit; font-family: Consolas, Menlo, &quot;courier new&quot;, monospace; font-size: 15px; color: rgb(165, 13, 13);">ejemplo_database</mark>.</p><pre class="wp-block" style="box-sizing: inherit; margin: 24px -20px; padding: 20px; border: 0px; background: rgb(244, 244, 244); font-family: inherit; font-size: inherit; line-height: normal; max-width: 100%; border-radius: 10px;"><pre class="wp-block-code" style="margin-top: 10px; margin-bottom: 10px; padding: 20px; border-width: 0px 0px 0px 4px; border-style: solid; border-color: rgb(255, 255, 255) rgb(255, 255, 255) rgb(255, 255, 255) rgb(4, 170, 109); border-image: initial; background: rgb(255, 255, 255); font-family: Consolas, Menlo, &quot;courier new&quot;, monospace; font-size: inherit; line-height: normal; max-width: 100%; color: rgb(0, 0, 0);"><code style="box-sizing: inherit; font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-variant-position: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-stretch: normal; font-size: 15px; line-height: normal; font-family: inherit; display: block; text-wrap: wrap;"><span class="sqlkeyword" style="box-sizing: inherit; color: rgb(59, 120, 231); line-height: 1.5;">CREATE DATABASE</span> ejemplo_database;</code></pre></pre><hr class="wp-block-separator has-text-color has-base-color has-alpha-channel-opacity has-base-background-color has-background" style="box-sizing: inherit; margin-top: 40px; margin-bottom: 40px; border-top: 2px solid; border-right-width: initial; border-right-style: none; border-bottom-width: initial; border-bottom-style: none; border-left-width: initial; border-left-style: none; background-color: var(--base); height: 1px;"><p class="has-background" style="box-sizing: inherit; margin-bottom: 1.5em; padding: 1.25em 2.375em; border: 0px; background-color: rgb(255, 249, 196);"><span style="box-sizing: inherit; font-weight: 700;">Importante</span>: Para crear una base de datos, el&nbsp;<span style="box-sizing: inherit; font-weight: 700;">usuario</span>&nbsp;que utilices necesita tener los&nbsp;<span style="box-sizing: inherit; font-weight: 700;">privilegios de administrador</span>&nbsp;para poder crear una base de datos.</p><p style="box-sizing: inherit; margin-bottom: 1.5em; border: 0px;">Una vez hayas creado una base de datos, puedes comprobar si ya existe con el siguiente comando SQL:&nbsp;<mark class="has-inline-color" style="box-sizing: inherit; font-family: Consolas, Menlo, &quot;courier new&quot;, monospace; font-size: 15px; color: rgb(165, 13, 13);">SHOW DATABASES</mark>;</p><p style="box-sizing: inherit; margin-bottom: 1.5em; border: 0px;"><span style="background-color: transparent;">Como ya hemos comentado antes, la sintaxis es igual en SQL Server, MySQL, PostgreSQL o MariaDB:</span></p><hr class="wp-block-separator has-text-color has-base-color has-alpha-channel-opacity has-base-background-color has-background" style="box-sizing: inherit; margin-top: 40px; margin-bottom: 40px; border-top: 2px solid; border-right-width: initial; border-right-style: none; border-bottom-width: initial; border-bottom-style: none; border-left-width: initial; border-left-style: none; background-color: var(--base); height: 1px;"><h3 class="wp-block-heading" style="box-sizing: inherit; margin-top: 20px; margin-bottom: 30px; border: 0px; font-family: inherit; font-size: 29px; font-style: inherit; line-height: 1.2em;">CREATE DATABASE en MySQL</h3><p style="box-sizing: inherit; margin-bottom: 1.5em; border: 0px;">Crear una base de datos en MySQL:</p><pre class="wp-block" style="box-sizing: inherit; margin: 24px -20px; padding: 20px; border: 0px; background: rgb(244, 244, 244); font-family: inherit; font-size: inherit; line-height: normal; max-width: 100%; border-radius: 10px;"><pre class="wp-block-code" style="margin-top: 10px; margin-bottom: 10px; padding: 20px; border-width: 0px 0px 0px 4px; border-style: solid; border-color: rgb(255, 255, 255) rgb(255, 255, 255) rgb(255, 255, 255) rgb(4, 170, 109); border-image: initial; background: rgb(255, 255, 255); font-family: Consolas, Menlo, &quot;courier new&quot;, monospace; font-size: inherit; line-height: normal; max-width: 100%; color: rgb(0, 0, 0);"><code style="box-sizing: inherit; font-variant-numeric: normal; font-variant-east-asian: normal; font-variant-alternates: normal; font-variant-position: normal; font-kerning: auto; font-optical-sizing: auto; font-feature-settings: normal; font-variation-settings: normal; font-stretch: normal; font-size: 15px; line-height: normal; font-family: inherit; display: block; text-wrap: wrap;"><span class="sqlkeyword" style="box-sizing: inherit; color: rgb(59, 120, 231); line-height: 1.5;">CREATE DATABASE</span> nombre_de_la_base_de_datos;</code></pre></pre><hr class="wp-block-separator has-text-color has-base-color has-alpha-channel-opacity has-base-background-color has-background" style="box-sizing: inherit; margin-top: 40px; margin-bottom: 40px; border-top: 2px solid; border-right-width: initial; border-right-style: none; border-bottom-width: initial; border-bottom-style: none; border-left-width: initial; border-left-style: none; background-color: var(--base); height: 1px;"><h3 class="wp-block-heading" style="box-sizing: inherit; margin-top: 20px; margin-bottom: 30px; border: 0px; font-family: inherit; font-size: 29px; font-style: inherit; line-height: 1.2em;">CREATE DATABASE en SQL Server</h3><p style="box-sizing: inherit; margin-bottom: 1.5em; border: 0px;">Como crear una base de datos en SQL Server:</p><pre class="